/**
 * Get muscle group data for a specific date
 * @param date - Date to look up (uses YYYY-MM-DD format internally)
 * @returns Muscle group data or null if date not found in calendar
 */
export function getMuscleGroups(date: Date): DayMuscleGroups | null {
  const key = date.toISOString().split('T')[0];
  return MUSCLE_GROUP_CALENDAR[key] || null;
}

/**
 * Get muscle group data for a date string in MM/DD/YYYY format
 * @param dateStr - Date string in MM/DD/YYYY format
 * @returns Muscle group data or null if date not found
 */
export function getMuscleGroupsFromString(dateStr: string): DayMuscleGroups | null {
  // Convert MM/DD/YYYY to YYYY-MM-DD
  const [month, day, year] = dateStr.split('/');
  const key = `${year}-${month.padStart(2, '0')}-${day.padStart(2, '0')}`;
  return MUSCLE_GROUP_CALENDAR[key] || null;
}
